mapper.select:
select board_no no, board_title title, board_content content, board_created_date createdDate, board_view_count viewCount from ( select A.board_no, A.board_title , A.board_content , A.board_created_date , A.board_view_count, row_number() over (order by board_no desc) rn from notice_board A
board_title like '%' || #{value} || '%'
board_content like '%' || #{value} || '%'
(board_title like '%' || #{value} || '%' or board_content like '%' || #{value} || '%')
) where rn between #{beginIndex} and #{endIndex}
