mapper.select:
select board_no no, board_title title, board_content content, board_created_date createdDate from ( select A.board_no no, A.board_title title, A.board_content content, A.board_created_date createdDate, row_number() over (order by book_no desc) rn from notice_board A
board_title like '%' || #{value} || '%'
board_content like '%' || #{value} || '%'
board_title like '%' || #{value} || '%' || board_content like '%' || #{value} || '%'
) where rn between #{beginIndex} and #{endIndex}
